list.definition:
from(bucket: "noaa-water-sample")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "average_temperature")
  |> filter(fn: (r) => r["_field"] == "degrees")
  |> unique(column: "location")
  |> map(fn: (r) => ({
                     foo: r.location,
                   }))

