// Version control Javascript in Grafana Scatterplot panel
// create vars
let country_name = data.series[0].fields[0].values.buffer
let current_year_population = data.series[0].fields[1].values.buffer
let population_growth_rate = data.series[0].fields[2].values.buffer
let average_life_expectancy = data.series[0].fields[3].values.buffer
let is_selected = data.series[0].fields[4].values.buffer

// create vars
let highlighted_opacity = 1
let not_selected_opacity = 0.2
let highlighted_color = 'rgb(184, 119, 217)'
let not_selected_color = 'rgb(101, 101, 101)'
let max_marker_size = 40

// Create default "template" for each country's trace

var trace = {
    mode: 'markers',
    name: [],
    text: [],

    // trace data
    x: [],
    y: [],
    marker: {
        color: [], // 'rgb(101, 101, 101)'
        size: [],
        sizemode: 'area',
        sizeref: 2.0 * Math.max(...current_year_population) / (max_marker_size ** 2),

        line: {
            color: 'rgb(81, 81, 81)',
            width: 1
        },
    },
    opacity: [], //0.2

}

// Loop through data and fill in trace

for (let i = 0; i < country_name.length; i++) {
    trace['name'].push(country_name[i])
    trace['text'].push(country_name[i])
    trace['x'].push(population_growth_rate[i])
    trace['y'].push(average_life_expectancy[i])
    trace['marker']['size'].push(current_year_population[i])

    if (is_selected[i] == 1) {
        trace['marker']['color'].push(highlighted_color)
        trace['opacity'].push(highlighted_opacity)
    } else {
        trace['marker']['color'].push(not_selected_color)
        trace['opacity'].push(not_selected_opacity)
    }
}

// set layout details
var layout = {
    margin: {
        l: 80,
        r: 80,
        b: 50,
        pad: 6
    },
    xaxis: {
        title: {
            text: 'Last year population growth rate',
            standoff: 20,
        },
        type: 'linear',
        tickformat: '.2%',
        dtick: 0.005,
        gridcolor: 'rgb(61,61,61)',
        gridwidth: 1,
    },
    yaxis: {
        title: {
            text: 'Average Life Expectancy',
            standoff: 10,
        },
        tickformat: '.2s',
        gridcolor: 'rgb(61,61,61)',
        gridwidth: 1,
    },
    showlegend: false,
    hovermode: 'closest'
}

return {
    data : [trace],
    layout: layout,
}
